import { Component, OnInit } from '@angular/core';
import { Booking } from '../booking';
import { BookingService } from '../Service/booking.service';
import { Router } from '@angular/router';
import { Customer } from '../customer';

@Component({
  selector: 'app-booking-list',
  templateUrl: './booking-list.component.html',
  styleUrls: ['./booking-list.component.css']
})
export class BookingListComponent implements OnInit{
  bookings!: Booking[];
  customers!: Customer[];
  constructor(public router:Router,private bookingService:BookingService){}

  ngOnInit(): void {

    this.getBookings();

  }


  getBookings(){
    this.bookingService.getBookings().subscribe(data=>{
      console.log(data);
      this.bookings=data;
    });
  }

  goToDashboard(){
    this.router.navigateByUrl("/dashboard");    
  }

}

<h2 class="text-center" >Transaction History</h2>
<table class="table table-striped">

    <thead class="table-danger" >

        <th>Book ID</th>
       <th>Customer ID</th>
        <th>Room No</th>
        <th>No of Days</th>
        <th>Room Price</th>
        <th>Total Amount</th>
    </thead>
    <tbody>

        <tr *ngFor ="let book of bookings" class="table-light">

            <td>{{book.bookId}}</td>
            <td>{{book.customerId}}</td>
            <td>{{book.roomNo}}</td>
            <td>{{book.noOfDays}}</td>
            <td>{{book.roomType}}</td>
            <td>{{book.totalAmount}}</td>

        </tr>


    </tbody>

</table>

<button (click)="goToDashboard()" class="btn btn-outline-primary">Go to Dashboard</button>

