  selector: 'app-roomtype',
  templateUrl: './roomtype.component.html',
  styleUrls: ['./roomtype.component.css']
})
export class RoomtypeComponent {
  customers!: Customer[]; 
  constructor(public router:Router){}

  goToDashboard(){
    this.router.navigateByUrl("/dashboard");    
