import { Component, OnInit } from '@angular/core';
import { Customer } from '../customer';
import { CustomerService } from '../Service/customer.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-customer-list',
  templateUrl: './customer-list.component.html',
  styleUrls: ['./customer-list.component.css']
})
export class CustomerListComponent implements OnInit{

  customers!: Customer[]; 

  constructor(public router:Router,private customerService:CustomerService){}

  ngOnInit(): void {

    this.getCustomer();

  }

  getCustomer(){
    this.customerService.getCustomer().subscribe(data=>{
      console.log(data);
      this.customers=data;
    });
  }

  addCustomer():void{
    this.router.navigateByUrl("addCustomer");

  }

  updateCustomer(id:number){
    this.router.navigateByUrl("/updateCustomer/"+id);

  }
  BookRoom(id:number){
    this.router.navigateByUrl("/bookRoom/"+id);

  }

  deleteCustomer(id:number):void
  {
    console.log(id);
    if(confirm("Do you want to delete ?")){
      this.customerService. deleteCustomer(id).subscribe(data=>{
        console.log(data);
        this.getCustomer();
      })
    };
  }

  goToDashboard(){
    this.router.navigateByUrl("/dashboard");    
  }
}
<h2 class="text-center">Customer List</h2>
<table class="table table-striped">

    <thead>
        <th>Customer ID</th>
        <th>Customer Name</th>
        <th>Customer EmailID</th>
        <th>Customer Contact No.</th>
        <th>Customer Address</th>
        <th>Username</th>
        <th>Password</th>
        <th>Actions</th>
    </thead>
    <tbody>

        <tr *ngFor ="let customer of customers" class="table-light">
            <td>{{customer.employeeId}}</td>
            <td>{{customer.employeeName}}</td>
            <td>{{customer.employeeEmailId}}</td>
            <td>{{customer.employeeContactNumber}}</td>
            <td>{{customer.employeeAddress}}</td>
            <td>{{customer.username}}</td>
            <td>{{customer.password}}</td>
            <td> <button (click)="updateCustomer(customer.employeeId)" class="btn btn-outline-primary">Update</button></td>
            <td> <button (click)="deleteCustomer(customer.employeeId)" class="btn btn-outline-primary">Delete</button></td>

            <td> <button (click)="BookRoom(customer.employeeId)" class="btn btn-outline-primary">Book Room</button></td>

        </tr>


    </tbody>

</table>

<button (click)="goToDashboard()" class="btn btn-outline-primary">Go to Dashboard</button>

